mapper.select:
select s.* from goods_post_cart gpc where 1=1

	and gpc.operator_id = #{page.c.operatorId,jdbcType=BIGINT}


	and gpc.code like concat(#{page.c.code,jdbcType=VARCHAR},'%')


	and gpc.short_name like concat('%',#{page.c.shortName,jdbcType=VARCHAR},'%')

limit #{page.start},#{page.count}
