mapper.update:
update supplier
set 
  group_id = #{groupId,jdbcType=BIGINT},
  name = #{name,jdbcType=VARCHAR},
  code = #{code,jdbcType=VARCHAR}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select id, group_id, name, code, create_time, update_time
from supplier
where id = #{id,jdbcType=BIGINT}
