mapper.update:
update sys_role
set role_name = #{roleName,jdbcType=VARCHAR},
  remark = #{remark,jdbcType=VARCHAR},
  dept_id = #{deptId,jdbcType=BIGINT}
where role_id = #{roleId,jdbcType=BIGINT}

mapper.select:
select role_id, role_name, remark, dept_id, create_time
from sys_role
where role_id = #{roleId,jdbcType=BIGINT}
